import React, { Component } from 'react';
import './App.css';
import { Row } from 'react-bootstrap';
import NavBar from './NavBar/NavBar';
import TitleComponent from './TitleComponent/TitleComponent';
import './TitleComponent/TitleComponent.css';
import ImageComponent from './ImageComponent/ImageComponent';
import images from './ImageComponent/images.json'

class App extends Component {
  state = {
    images,
    score: 0,
    message: 'Click a picture to score points but never under any circumstance click the same picture twice or your computer will self destruct like inspector gadget style!'

  };

  imageClickHandler = () => {
    // We always use the setState method to update a component's state
    console.log(this.state.score)
    this.setState({
      score: this.state.score + 1,
      message: `you have ${this.state.score} out of 12 points.  Way to go!`
    });

  }
  render() {
    return (
      <div className="App">

        <Row>
          <NavBar
            score={this.state.score}
            message={this.state.message}
            />

        </Row>

        <Row>
          <TitleComponent />
        </Row>

        <Row className='image-container'>
          {this.state.images.map(images => (
            <ImageComponent
              key={images.id}
              name={images.name}
              image={images.image}
              imageClickHandler={this.imageClickHandler}
            />
          ))}
        </Row>

      </div>
    )
  }
}

export default App;
